import 'package:flutter/material.dart';
import 'package:project/release_model.dart';

import 'artist_model.dart';

//music state for ui details
class MusicState with ChangeNotifier{
  List<String> _tabs= ['relax','party','work out'];
  List <Artist> _artists= [Artist(name: 'A R Rahuman', listeners: '100M listeners', imageUrl: 'data:image/webp;base64,'),
  ];
  List<Release> _releases = [
    Release(title: 'Melodies', artist: 'SPB', imageUrl: 'data:image/jpeg;base64,'),
    Release(title: 'Middle of the Night', artist: 'shreya ghoshal', imageUrl: 'data:image/webp;base64,'),
  ];

  List<String> get tabs => _tabs;
  List<Artist> get artists => _artists;
  List<Release> get releases => _releases;

  void addArtist(Artist artist) {
    _artists.add(artist);
    notifyListeners();
  }

  void addRelease(Release release) {
    _releases.add(release);
    notifyListeners();
  }

}